import axios from 'axios';
import { YoutubeTranscript } from 'youtube-transcript';

// Your YouTube API key
const YOUTUBE_API_KEY = 'AIzaSyApXWAoAYayJouq0iKRlMj-3R61VRLqkCE';

interface VideoResponse {
  items: Array<{
    id: string;
    snippet: {
      title: string;
      description: string;
    };
  }>;
}

interface TranscriptResponse {
  transcript: string;
}

export const extractVideoId = (url: string): string | null => {
  try {
    // Handle different YouTube URL formats
    const patterns = [
      /(?:youtube\.com\/watch\?v=|youtu\.be\/|youtube\.com\/embed\/|youtube\.com\/v\/|youtube\.com\/watch\?.*&v=)([^#&?]*).*/,
      /youtube\.com\/shorts\/([^#&?]*).*/,
      /youtube\.com\/watch\?.*v=([^#&?]*).*/
    ];

    for (const pattern of patterns) {
      const match = url.match(pattern);
      if (match && match[1] && match[1].length === 11) {
        return match[1];
      }
    }

    // If no pattern matches, try to extract from the URL directly
    const urlObj = new URL(url);
    const searchParams = new URLSearchParams(urlObj.search);
    const videoId = searchParams.get('v');
    
    if (videoId && videoId.length === 11) {
      return videoId;
    }

    return null;
  } catch (error) {
    console.error('Error extracting video ID:', error);
    return null;
  }
};

export const getVideoTranscript = async (videoId: string): Promise<string> => {
  try {
    // First, get video details to check if video exists
    const videoResponse = await axios.get<VideoResponse>(
      `https://www.googleapis.com/youtube/v3/videos?part=snippet&id=${videoId}&key=${YOUTUBE_API_KEY}`
    );

    if (!videoResponse.data.items?.length) {
      throw new Error('Video not found. Please check the URL and try again.');
    }

    try {
      // Try to get transcript without any language specification first
      const transcriptItems = await YoutubeTranscript.fetchTranscript(videoId);
      
      if (!transcriptItems || transcriptItems.length === 0) {
        // If no transcript found, try with English
        const englishTranscript = await YoutubeTranscript.fetchTranscript(videoId, {
          lang: 'en'
        });

        if (!englishTranscript || englishTranscript.length === 0) {
          throw new Error('No captions found for this video. Please try a video with captions enabled.');
        }

        return englishTranscript.map(item => item.text).join(' ');
      }

      // Combine all transcript segments into one string
      const transcript = transcriptItems
        .map(item => item.text)
        .join(' ');

      return transcript;
    } catch (transcriptError: any) {
      console.error('Transcript fetch error:', transcriptError);
      
      // Try alternative method if the first one fails
      try {
        const response = await axios.get<TranscriptResponse>(
          `https://youtube-transcript-api.vercel.app/api/transcript?videoId=${videoId}`
        );
        
        if (response.data?.transcript) {
          return response.data.transcript;
        }
      } catch (fallbackError) {
        console.error('Fallback transcript fetch error:', fallbackError);
      }
      
      // Provide more specific error messages based on the error
      if (transcriptError.message?.includes('Could not get the transcript')) {
        throw new Error('This video does not have captions available. Please try a different video.');
      }
      if (transcriptError.message?.includes('Could not find a transcript')) {
        throw new Error('No captions found for this video. Please try a video with captions enabled.');
      }
      
      throw new Error('Could not fetch transcript. Please make sure the video has captions available.');
    }
  } catch (error: any) {
    console.error('Error:', error);
    if (error?.response?.status === 403) {
      throw new Error('API key is invalid or quota exceeded. Please check your YouTube API configuration.');
    }
    if (error?.response?.status === 404) {
      throw new Error('Video not found. Please check the URL and try again.');
    }
    throw error; // Re-throw the original error to preserve the specific error message
  }
};
