import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { getTutorProfile } from '../services/profileService';
import { generateTutorResponse } from '../services/llmService';
import ChatUI from '../components/ChatUI';

interface Profile {
  name: string;
  style: string;
  characteristics: string[];
  tone: string;
  useAnalogies: boolean;
  stepByStep: boolean;
}

interface Message {
  role: 'user' | 'assistant';
  content: string;
}

const TutorChat: React.FC = () => {
  const navigate = useNavigate();
  const [messages, setMessages] = useState<Message[]>([]);
  const [isLoading, setIsLoading] = useState(false);
  const [profile, setProfile] = useState<Profile | null>(null);
  const [isLoadingProfile, setIsLoadingProfile] = useState(true);
  const [error, setError] = useState<string | null>(null);

  useEffect(() => {
    let isMounted = true;

    const loadProfile = async () => {
      try {
        const savedProfile = await getTutorProfile();
        if (!isMounted) return;

        if (!savedProfile) {
          setError('No profile found. Please create a new profile.');
          navigate('/create');
          return;
        }
        setProfile(savedProfile);
        setError(null);
      } catch (error) {
        if (!isMounted) return;
        console.error('Error loading profile:', error);
        setError('Failed to load profile. Please try again.');
        navigate('/create');
      } finally {
        if (isMounted) {
          setIsLoadingProfile(false);
        }
      }
    };

    loadProfile();

    return () => {
      isMounted = false;
    };
  }, [navigate]);

  const handleSendMessage = async (content: string) => {
    if (!profile) return;

    const newMessage: Message = { role: 'user', content };
    setMessages(prev => [...prev, newMessage]);
    setIsLoading(true);

    try {
      const response = await generateTutorResponse(content, profile);
      setMessages(prev => [...prev, { role: 'assistant', content: response }]);
    } catch (error) {
      console.error('Error generating response:', error);
      setError('Failed to generate response. Please try again.');
    } finally {
      setIsLoading(false);
    }
  };

  if (isLoadingProfile) {
    return (
      <div className="flex justify-center items-center min-h-screen">
        <div className="text-center">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-primary mx-auto mb-4"></div>
          <p className="text-gray-600">Loading your profile...</p>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="flex justify-center items-center min-h-screen">
        <div className="text-center">
          <p className="text-red-600 mb-4">{error}</p>
          <button
            onClick={() => navigate('/create')}
            className="bg-primary text-white px-4 py-2 rounded hover:bg-primary-dark"
          >
            Create New Profile
          </button>
        </div>
      </div>
    );
  }

  if (!profile) {
    return null;
  }

  return (
    <div className="container mx-auto px-4 py-8">
      <h1 className="text-3xl font-bold text-center mb-8">Chat with Your AI Tutor</h1>
      <div className="max-w-4xl mx-auto">
        <ChatUI
          messages={messages}
          onSendMessage={handleSendMessage}
          isLoading={isLoading}
        />
      </div>
    </div>
  );
};

export default TutorChat;
