import axios from 'axios';

interface TutorProfile {
  name: string;
  style: string;
  tone: string;
  strengths: string[];
  specializations: string[];
  useAnalogies: boolean;
  stepByStep: boolean;
}

interface ProfileResponse {
  profile: TutorProfile;
}

const API_URL = 'http://localhost:5000/api';

// Generate a unique session ID
const generateSessionId = () => {
  return Math.random().toString(36).substring(2) + Date.now().toString(36);
};

// Get or create session ID
const getSessionId = () => {
  let sessionId = localStorage.getItem('sessionId');
  if (!sessionId) {
    sessionId = generateSessionId();
    localStorage.setItem('sessionId', sessionId);
  }
  return sessionId;
};

export const saveTutorProfile = async (profile: TutorProfile): Promise<void> => {
  try {
    const sessionId = getSessionId();
    await axios.post(`${API_URL}/profile`, { profile, sessionId });
  } catch (error) {
    console.error('Error saving tutor profile:', error);
    throw new Error('Failed to save tutor profile');
  }
};

export const getTutorProfile = async (): Promise<TutorProfile | null> => {
  try {
    const sessionId = getSessionId();
    const response = await axios.get<ProfileResponse>(`${API_URL}/profile/${sessionId}`);
    return response.data.profile;
  } catch (error) {
    if (error && typeof error === 'object' && 'response' in error && error.response?.status === 404) {
      return null;
    }
    console.error('Error getting tutor profile:', error);
    throw new Error('Failed to get tutor profile');
  }
};

export const hasTutorProfile = async (): Promise<boolean> => {
  try {
    const profile = await getTutorProfile();
    return profile !== null;
  } catch (error) {
    console.error('Error checking tutor profile:', error);
    return false;
  }
};

export const clearTutorProfile = async (): Promise<void> => {
  try {
    const sessionId = getSessionId();
    localStorage.removeItem('sessionId');
  } catch (error) {
    console.error('Error clearing tutor profile:', error);
    throw new Error('Failed to clear tutor profile');
  }
};
