import React, { useState } from 'react';
import * as pdfjsLib from 'pdfjs-dist';
import { extractVideoId, getVideoTranscript } from '../services/youtubeService';

interface UploadFormProps {
  onUpload: (content: string) => void;
  isLoading: boolean;
}

const UploadForm: React.FC<UploadFormProps> = ({ onUpload, isLoading }) => {
  const [youtubeUrl, setYoutubeUrl] = useState('');
  const [pdfFile, setPdfFile] = useState<File | null>(null);
  const [error, setError] = useState<string | null>(null);

  const handleYoutubeSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    if (!youtubeUrl) return;

    setError(null);
    const videoId = extractVideoId(youtubeUrl);
    
    if (!videoId) {
      setError('Invalid YouTube URL. Please enter a valid YouTube video URL.');
      return;
    }

    try {
      const transcript = await getVideoTranscript(videoId);
      onUpload(transcript);
    } catch (error) {
      setError(error instanceof Error ? error.message : 'Failed to process YouTube video');
    }
  };

  const handlePdfUpload = async (e: React.ChangeEvent<HTMLInputElement>) => {
    const file = e.target.files?.[0];
    if (!file) return;

    setPdfFile(file);
    const reader = new FileReader();

    reader.onload = async (event) => {
      const typedArray = new Uint8Array(event.target?.result as ArrayBuffer);
      const pdf = await pdfjsLib.getDocument(typedArray).promise;
      let fullText = '';

      for (let i = 1; i <= pdf.numPages; i++) {
        const page = await pdf.getPage(i);
        const textContent = await page.getTextContent();
        const pageText = textContent.items.map((item: any) => item.str).join(' ');
        fullText += pageText + '\n';
      }

      onUpload(fullText);
    };

    reader.readAsArrayBuffer(file);
  };

  return (
    <div className="space-y-6">
      <div className="bg-white p-6 rounded-lg shadow-md">
        <h2 className="text-xl font-semibold mb-4">Upload YouTube Video</h2>
        <form onSubmit={handleYoutubeSubmit} className="space-y-4">
          <input
            type="url"
            value={youtubeUrl}
            onChange={(e) => setYoutubeUrl(e.target.value)}
            placeholder="Enter YouTube URL"
            className="w-full px-4 py-2 border rounded-md focus:outline-none focus:ring-2 focus:ring-primary"
            disabled={isLoading}
          />
          {error && (
            <p className="text-red-500 text-sm">{error}</p>
          )}
          <button
            type="submit"
            disabled={isLoading || !youtubeUrl}
            className="w-full bg-primary text-white py-2 px-4 rounded-md hover:bg-primary/90 disabled:opacity-50"
          >
            {isLoading ? 'Processing...' : 'Analyze Video'}
          </button>
        </form>
      </div>

      <div className="bg-white p-6 rounded-lg shadow-md">
        <h2 className="text-xl font-semibold mb-4">Upload PDF</h2>
        <div className="space-y-4">
          <input
            type="file"
            accept=".pdf"
            onChange={handlePdfUpload}
            className="w-full"
            disabled={isLoading}
          />
          {pdfFile && (
            <p className="text-sm text-gray-600">
              Selected file: {pdfFile.name}
            </p>
          )}
        </div>
      </div>
    </div>
  );
};

export default UploadForm;
