import React, { useState } from 'react';
import * as pdfjsLib from 'pdfjs-dist';
import { getVideoTranscript, analyzeTranscript } from '../services/youtubeService';

interface UploadFormProps {
  onAnalysisComplete: (transcript: string) => Promise<void>;
}

const UploadForm: React.FC<UploadFormProps> = ({ onAnalysisComplete }) => {
  const [url, setUrl] = useState('');
  const [error, setError] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [pdfFile, setPdfFile] = useState<File | null>(null);
  const [transcript, setTranscript] = useState<string>('');
  const [analysis, setAnalysis] = useState<string>('');
  const [isAnalyzing, setIsAnalyzing] = useState(false);

  const handleYoutubeSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    setError('');
    setIsLoading(true);

    try {
      const transcript = await getVideoTranscript(url);
      await onAnalysisComplete(transcript);
    } catch (error: any) {
      setError(error.message || 'Failed to process video');
    } finally {
      setIsLoading(false);
    }
  };

  const handleAnalyze = async () => {
    if (!transcript) return;

    setIsAnalyzing(true);
    setError(null);
    setAnalysis('');

    try {
      const analysisText = await analyzeTranscript(transcript);
      setAnalysis(analysisText);
    } catch (error) {
      setError(error instanceof Error ? error.message : 'Failed to analyze transcript');
    } finally {
      setIsAnalyzing(false);
    }
  };

  const handlePdfUpload = async (e: React.ChangeEvent<HTMLInputElement>) => {
    const file = e.target.files?.[0];
    if (!file) return;

    setPdfFile(file);
    const reader = new FileReader();

    reader.onload = async (event) => {
      const typedArray = new Uint8Array(event.target?.result as ArrayBuffer);
      const pdf = await pdfjsLib.getDocument(typedArray).promise;
      let fullText = '';

      for (let i = 1; i <= pdf.numPages; i++) {
        const page = await pdf.getPage(i);
        const textContent = await page.getTextContent();
        const pageText = textContent.items.map((item: any) => item.str).join(' ');
        fullText += pageText + '\n';
      }

      await onAnalysisComplete(fullText);
    };

    reader.readAsArrayBuffer(file);
  };

  return (
    <div className="space-y-6">
      <div className="bg-white p-6 rounded-lg shadow-md">
        <h2 className="text-xl font-semibold mb-4">Upload YouTube Video</h2>
        <form onSubmit={handleYoutubeSubmit} className="space-y-4">
          <div>
            <label htmlFor="youtube-url" className="block text-sm font-medium text-gray-700">
              YouTube Video URL
            </label>
            <input
              type="text"
              id="youtube-url"
              value={url}
              onChange={(e) => setUrl(e.target.value)}
              placeholder="Enter YouTube video URL"
              className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-blue-500 focus:ring-blue-500"
              required
            />
          </div>

          <button
            type="submit"
            disabled={isLoading}
            className={`w-full flex justify-center py-2 px-4 border border-transparent rounded-md shadow-sm text-sm font-medium text-white bg-blue-600 hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500 ${
              isLoading ? 'opacity-50 cursor-not-allowed' : ''
            }`}
          >
            {isLoading ? 'Processing...' : 'Analyze Video'}
          </button>

          {error && (
            <div className="mt-4 p-4 bg-red-100 text-red-700 rounded">
              {error}
            </div>
          )}
        </form>
        {transcript && (
          <div className="mt-4">
            <h3 className="text-lg font-semibold mb-2">Transcript:</h3>
            <textarea
              value={transcript}
              readOnly
              className="w-full h-64 p-4 border rounded-md bg-gray-50 font-mono text-sm whitespace-pre-wrap"
              style={{ lineHeight: '1.5' }}
            />
            <button
              onClick={handleAnalyze}
              disabled={isAnalyzing}
              className="mt-4 w-full bg-secondary text-white py-2 px-4 rounded-md hover:bg-secondary/90 disabled:opacity-50"
            >
              {isAnalyzing ? 'Analyzing...' : 'Analyze Transcript'}
            </button>
          </div>
        )}
        {analysis && (
          <div className="mt-4">
            <h3 className="text-lg font-semibold mb-2">Teaching Style Analysis:</h3>
            <div className="p-4 border rounded-md bg-gray-50 whitespace-pre-wrap text-sm">
              {analysis.split('\n').map((line, index) => {
                // Style section headers
                if (line.match(/^\d+\./)) {
                  return (
                    <div key={index} className="font-bold text-primary mt-4 mb-2">
                      {line}
                    </div>
                  );
                }
                // Style bullet points
                if (line.startsWith('-')) {
                  return (
                    <div key={index} className="ml-4 mb-1">
                      {line}
                    </div>
                  );
                }
                // Style timestamps
                if (line.match(/\[\d{2}:\d{2}\]/)) {
                  return (
                    <div key={index} className="text-secondary font-mono mb-1">
                      {line}
                    </div>
                  );
                }
                // Regular text
                return <div key={index} className="mb-2">{line}</div>;
              })}
            </div>
          </div>
        )}
      </div>

      <div className="bg-white p-6 rounded-lg shadow-md">
        <h2 className="text-xl font-semibold mb-4">Upload PDF</h2>
        <div className="space-y-4">
          <input
            type="file"
            accept=".pdf"
            onChange={handlePdfUpload}
            className="w-full"
            disabled={isLoading}
          />
          {pdfFile && (
            <p className="text-sm text-gray-600">
              Selected file: {pdfFile.name}
            </p>
          )}
        </div>
      </div>
    </div>
  );
};

export default UploadForm;
